import React from "react";
import PostHeader from "./PostHeader";
import styled from 'styled-components';

const CommentWrapper = styled.div`
    border-bottom: thin #E1E1E1 solid;
    margin-left: -16px;
    margin-right: -16px;    
    padding: 16px;
`;

const CommentBody = styled.div`
    font-size: 12px;
`;

export default function Comment({ commentData, postId, setPosts }) {
    const { comment, commenter, id } = commentData;

    function deleteComment() {
        setPosts(prevPosts => prevPosts.map(post => post.id === postId ?
            // locate the current post, spread in existing props
            { 
                ...post, 
                // target comments and return an array that removes the comment we want to delete
                comments: post.comments.filter(comment => comment.id !== id)
            }
            :
            post
        ));
    };

    return (
        <CommentWrapper>
            <PostHeader user={commenter} handleDelete={deleteComment} />
            <CommentBody>{comment}</CommentBody>
        </CommentWrapper>
    );    
}
