                {likes} {likes === 1 ? "like" : "likes"}
                <div>{liked ? "‚ù§Ô∏è Un-like" : "ü§ç Like"}</div>
            </div>
            <div className="post-caption">
                {caption}
            </div>
        </div>
    ); 
};
