import React, { createContext, useState } from "react";
import axios from "axios";

export const AuthenticationContext = createContext();

export const AuthenticationContextProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState(false);

    // https://developer.mozilla.org/en-US/docs/Web/API/Fetch_API/Using_Fetch
    // Example POST method implementation:
    async function loequest(event) {
        event.preventDefault();
        const data = { 
            "user": {
                "email": "alice@example.com",
                "password": "password"
            }
        };

        const response = await fetch("https://adm-photogram.herokuapp.com/users/sign_in.json", {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            body: JSON.stringify(data) // body data type must match "Content-Type" header
        });
        return console.log(response.json()); // parses JSON response into native JavaScript objects
    };

    function loginRequest(event) {
        event.preventDefault();

        const credentials = { 
            "email": "alice@example.com",
            "password": "password"
        };

        axios.post("https://adm-photogram.herokuapp.com/users/sign_in.json", { user: credentials })
        .then((response) => {
            console.log(response.data);
        });
    }

    return (
        <AuthenticationContext.Provider
            value={{
                isAuthenticated: !!currentUser,
                currentUser, 
                loginRequest 
            }}
        >
            {children}
        </AuthenticationContext.Provider>        
    );
};
