import React, { createContext, useEffect, useState } from "react";
import axios from "axios";

export const FeedContext = createContext();

export const FeedContextProvider = ({ children }) => {
    const [feed, setFeed] = useState({});
    const [loaded, setLoaded] = useState(false);

    function getFeed(userId) {
        axios.get(`https://adm-photogram.herokuapp.com/api/v1/users/${userId}.json?include=feed.likes`)
        .then(response => {
            setFeed(response.data.data.feed); 
            setLoaded(true);
        });
    }

    function unlike() {
        const url = "https://adm-photogram.herokuapp.com/api/v1/likes";
        const data = 
        axios.post("https://adm-photogram.herokuapp.com/api/v1/likes", 
            {   likes: 
                {
                    fan_id: 118,
                    photo_id: 628
                }
            })
        .then((response) => {
            console.log(response);
        })
        .catch(response => console.log(response));
    }

    function like() {
        const url = "https://adm-photogram.herokuapp.com/api/v1/likes";
        const options = {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json;charset=UTF-8",
            },
            body: JSON.stringify({
                "type": "likes",
                "attributes": {
                    "fan_id": "118",
                    "photo_id": "629"
                }    
            })
        };
        fetch(url, options)
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
            });
    }

    return (
        <FeedContext.Provider
            value={{
                feed,
                getFeed,
                loaded,
                like
            }}
        >
            {children}
        </FeedContext.Provider>        
    );
};
