import React from "react";
import styled from "styled-components";
import PostHeader from "./PostHeader";
import Comment from "./Comment";
import CommentForm from "./CommentForm";

const PostWrapper = styled.div`
    display: flex;
    flex-direction: column;
    border: 1px solid #E1E1E1;
    border-radius: 4px;
    margin-top: 16px;
    padding: 16px;
`;

const PostImageWrapper = styled.div`
    margin-left: -16px;
    margin-right: -16px;
`;

const PostImage = styled.img`
    width: 100%;
`;

const LikesRow = styled.div`
    display: flex;
    flex-direction: row;
    gap: 8px;
    margin-top: 8px;
    font-size: 12px;
`;

const PostBody = styled.div`
    font-size: 12px;
    margin-top: 24px;
`;

export default function Post({ post, setPosts }) {
    const { comments, id, user, likes, liked, body, photo } = post;

    function deletePost() {
        setPosts(prevPosts => prevPosts.filter(post => post.id !== id))
    }

    function likePost() {
        setPosts(prevPosts => prevPosts.map(post => post.id === id ?
            // change the liked property 
            {
                // object spread - spread in existing object properties
                ...post,
                // target specific property we want to change
                liked: !post.liked
            }
            :
            // return the post object unchanged
            post
        ))  
    };

    const commentElements = comments.map((comment) => {
        return (
            <Comment commentData={comment} key={comment.id} postId={id} setPosts={setPosts} />
        );
    });

    return (
        <PostWrapper>
            <PostHeader user={user} handleDelete={deletePost} />
            <PostImageWrapper>
                <PostImage src={photo} />
            </PostImageWrapper>
            <LikesRow>
                {likes} {likes === 1 ? "like" : "likes"}
                <div onClick={likePost}>{liked ? "‚ù§Ô∏è Un-like" : "ü§ç Like"}</div>
            </LikesRow>                           
            <PostBody>{body}</PostBody>
            {commentElements}
            <CommentForm postId={id} setPosts={setPosts} />
        </PostWrapper>
    )
};
