import React from "react";
import styled from 'styled-components';

const PostHeaderWrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding-bottom: 16px;
`;

const PostHeaderSection = styled.div`
    display: flex;
    gap: 8px;
    font-size: 12px;
    font-weight: bold;
    align-items: center;
`;

export default function PostHeader({ user, handleDelete }) {
    return (
        <PostHeaderWrapper>
            <PostHeaderSection>
                <i className="fas fa-user-circle fa-lg icon"></i>
                {user}
            </PostHeaderSection>
            <PostHeaderSection style={{ color: "#6E757C" }}>
                <i className="fas fa-edit fa-xs"></i>
                <div onClick={handleDelete}>
                    <i className="fas fa-trash fa-xs"></i>
                </div>
            </PostHeaderSection>
        </PostHeaderWrapper>
    );
}
