import React from "react";
import styled from "styled-components";

const TabWrapper = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
`;

const Badge = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #6E757C;
    color: white;
    border-radius: 100px;
    height: 14px;
    width: 16px; 
    margin-left: 4px;
    font-size: 9px;
`;

export default function Tabs({ tab, setTab, postCount, likedPostCount }) {

    return (
        <TabWrapper>
            <div 
                className={tab === "Posts" ? "tab-button-selected" : "tab-button-unselected"} 
                onClick={() => setTab("Posts")}
            >
                Posts <Badge className="badge"> {postCount} </Badge>
            </div>
            <div 
                className={tab === "Liked" ? "tab-button-selected" : "tab-button-unselected"} 
                onClick={() => setTab("Liked")}
            >
                Likes <Badge className="badge"> {likedPostCount} </Badge>
            </div>
        </TabWrapper>
    )
}
