import React, { useState } from "react";
import styled from 'styled-components';

const CommentFormWrapper = styled.div`
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 16px;
`

const CommentButton = styled.button`
    background-color: white;
    border: thin #6E757C solid;
    border-radius: 4px;
    color: #6E757C;
    font-size: 12px;
    padding: 4px;

    &:hover {
        background-color: #F8F9FA;
    }
`;

const CommentInput = styled.input`
    border: thin #6E757C solid;
    border-radius: 4px;
    font-size: 12px;
    padding: 12px;
`;

export default function CommentForm({ postId, setPosts }) {
    const [newComment, setNewComment] = useState("");

    function createComment() {
        event.preventDefault();
        setPosts(prevPosts => prevPosts.map(post => post.id === postId ?
            // locate our post by post id
                {
                    // spread in the post properties
                    ...post,
                    // target the comment property, which is an array
                    comments: [
                        // add a new comment object to the comments array
                        { 
                            id: post.comments[post.comments.length - 1]+ 1,
                            commenter: "stephen_avocado",
                            comment: newComment
                        }, 
                        // spread in the existing comments
                        ...post.comments
                    ]
                }
                :
                post
        ));
        // after we add a comment, we want to clear out the comment form
        setNewComment("");
    };

    // this function is created to handle the state our comment input 
    function handleChange(event) {
        event.preventDefault();
        setNewComment(event.target.value);
    }

    return (
        <CommentFormWrapper>
            <CommentInput 
                className="comment-input" 
                type="text" 
                value={newComment} 
                onChange={handleChange}
            />
            <CommentButton onClick={createComment} >Create Comment</CommentButton>
        </CommentFormWrapper>
    );
};
